"use client";
import { CustomerFooter, CustomerHeader } from "@/partials";
import { useSession, signIn } from "next-auth/react";
import { useState, useEffect } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useRouter, useSearchParams } from "next/navigation";
import { AUTH_URL } from "@/utils/constants/urls";
import Image from "next/image";
import BlogListing from "@/components/(home)/blog-listing";
import ContactSection from "@/components/(home)/contact-section";
import ProductSlider from "@/components/(home)/product-slider";

export default function Home() {
  const { data: session } = useSession();
  const router = useRouter();
  const searchParams = useSearchParams();

  useEffect(() => {
    const token = searchParams?.get("token");
    if (token && !session) {
      const fetchUserAndSignIn = async () => {
        try {
          // Gọi API để lấy thông tin user
          const res = await fetch(`${AUTH_URL}/me`, {
            method: "GET",
            headers: {
              Authorization: "Bearer " + token,
            },
          });

          const response = await res.json();

          if (res.ok && response.success && response.data) {
            const { user, refreshToken, expiresIn } = response.data;

            // Đăng nhập với NextAuth sử dụng thông tin user
            const result = await signIn("credentials", {
              token,
              id: user.id,
              name: user.name,
              email: user.email,
              role: user.role,
              userAvt: user.user_avt,
              refreshToken,
              expiresIn,
              redirect: false,
            });

            if (result?.error) {
              toast.error("Đăng nhập thất bại");
              return;
            }

            // Xóa token khỏi URL
            window.history.replaceState({}, "", "/");
          } else {
            throw new Error(
              response.message || "Không thể lấy thông tin người dùng"
            );
          }
        } catch (error) {
          console.error("Login error:", error);
          toast.error("Có lỗi xảy ra khi đăng nhập");
          router.push("/login");
        }
      };

      fetchUserAndSignIn();
    }
  }, [searchParams, session, router]);

  return (
    <div className="w-full">
      <CustomerHeader />
      <div className="w-full flex flex-col">
        <div className="w-full relative">
          <Image
            src="/imgs/home/thumb.png"
            alt="thumb image"
            width={0}
            height={0}
            sizes="100vw"
            className="w-full h-auto object-cover"
            priority
            placeholder="blur"
            blurDataURL="data:image/jpeg;base64,"
          />
        </div>
        <ProductSlider />
        <div className="w-full relative">
          <Image
            src="/imgs/home/poster-1.png"
            alt="thumb image"
            width={0}
            height={0}
            sizes="100vw"
            className="w-full h-auto object-cover"
            priority
            placeholder="blur"
            blurDataURL="data:image/jpeg;base64,"
          />
        </div>
        <div className="w-full relative">
          <Image
            src="/imgs/home/main-feature.png"
            alt="thumb image"
            width={0}
            height={0}
            sizes="100vw"
            className="w-full h-auto object-cover"
            priority
            placeholder="blur"
            blurDataURL="data:image/jpeg;base64,"
          />
        </div>
        <div className="w-full relative">
          <Image
            src="/imgs/home/client-feedback.png"
            alt="thumb image"
            width={0}
            height={0}
            sizes="100vw"
            className="w-full h-auto object-cover"
            priority
            placeholder="blur"
            blurDataURL="data:image/jpeg;base64,"
          />
        </div>
        <div className="w-full relative">
          <Image
            src="/imgs/home/survey.png"
            alt="thumb image"
            width={0}
            height={0}
            sizes="100vw"
            className="w-full h-auto object-cover"
            priority
            placeholder="blur"
            blurDataURL="data:image/jpeg;base64,"
          />
        </div>
        <div className="w-full relative mx-auto bg-pri-5">
          <BlogListing></BlogListing>
        </div>
        <ContactSection />
      </div>
      <CustomerFooter />
      <ToastContainer />
    </div>
  );
}
